mapper.delete:
UPDATE board_cafe_comment
SET deleted='yes'
WHERE num=#{num}

mapper.update:
UPDATE board_cafe_comment
SET content=#{content}
WHERE num=#{num}

mapper.select:
SELECT NVL(MAX(ROWNUM), 0)
FROM comment_like
WHERE num=#{num} AND id=#{id} AND seq=#{ref_group}

SELECT com_likeCount
FROM board_cafe_comment
WHERE seq=#{ref_group} AND num=#{num}

mapper.update:
UPDATE board_cafe_comment
SET com_likeCount=com_likeCount+1
WHERE ref_group=#{ref_group} AND num=#{num}

UPDATE board_cafe_comment
SET com_likeCount=com_likeCount-1
WHERE ref_group=#{ref_group} AND num=#{num}

mapper.select:
SELECT id
FROM comment_like
WHERE seq=#{seq} AND id=#{id} And num=#{num}
