mapper.select:
SELECT seq, title, startdate, enddate, place, realmname, thumbnail, gpsx, gpsy, likecount
FROM tb_api_date
WHERE seq=#{seq}

SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT seq, title, TO_CHAR(startdate,'YYYY/MM/DD') AS startdate, TO_CHAR(enddate,'YYYY/MM/DD') AS enddate, place, thumbnail, likecount FROM tb_api_date
WHERE startdate>=to_date(${startdate},'yyyymmdd') AND startdate<=to_date(${enddate},'yyyymmdd')
WHERE title LIKE '%${title}%'
WHERE place LIKE '%${place}%'
order by startdate DESC
) result1) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

SELECT NVL(MAX(ROWNUM), 0)
FROM exhibition_like
WHERE seq=#{seq} AND id=#{id,jdbcType=VARCHAR}

SELECT likecount
FROM tb_api_date
WHERE seq=#{seq}

mapper.update:
UPDATE tb_api_date
SET likecount=likecount+1
WHERE seq=#{seq}

UPDATE tb_api_date
SET likecount=likeCount-1
WHERE seq=#{seq}

mapper.select:
SELECT id
FROM exhibition_like
WHERE seq=#{seq} AND id=#{id}
